import React, { useState } from 'react';

export default function Login() {
    const LoginForm = () => {
        const [formData, setFormData] = useState({
            username: '',
            password: '',
        });

        const handleInputChange = (e) => {
            const { name, value } = e.target;
            setFormData({
                ...formData,
                [name]: value,
            });
        };

        const handleSubmit = async () => {
            try {
                // Validate form data if needed

                // Send a POST request to the backend authentication endpoint
                const response = await fetch('http://localhost:3001/auth/login', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(formData),
                });

                if (response.ok) {
                    // Successful login
                    console.log('Login successful');
                    // You may want to redirect the user or perform additional actions here
                } else {
                    // Failed login
                    console.log('Login failed');
                    // You may want to display an error message to the user
                }
            } catch (error) {
                console.error('Error during login:', error);
            }
        };
        return (
            <>
                <div className="container">
                    <form action="index.html" method="get" className="form-signin">
                        <h2 className="form-signin-heading text-center">DCX CMS</h2>
                        <label htmlFor="inputEmail" className="sr-only">
                            Email address
                        </label>
                        <input
                            type="email"

                            id="inputEmail"
                            className="form-control"
                            placeholder="Email address"
                            name="username"
                            value={formData.email}
                            onChange={handleInputChange}
                            required
                            autoFocus
                        />
                        <label htmlFor="inputPassword" className="sr-only">
                            Password
                        </label>
                        <input
                            type="password"
                            id="inputPassword"
                            className="form-control"
                            placeholder="Password"
                            name="password"
                            value={formData.password}
                            onChange={handleInputChange}
                            required
                        />
                        <div className="checkbox">
                            <label>
                                <input type="checkbox" value="remember-me" /> Remember me
                            </label>
                        </div>
                        <button onClick={handleSubmit} className="btn btn-lg btn-primary btn-block" type="submit">
                            Sign in
                        </button>
                    </form>
                </div>
            </>
        );
    }
}
